import React from "react";
import { useState } from "react";

const App = () =>{
  const [value,setValue] = useState("")
  //value는 username이다.
  const onChange = (event: React.FormEvent<HTMLInputElement>) => {};
  //위에와는 다른 새로운 함수이다.
  //event는 아래 input의 이벤트임으로 event자체가 변수라고 생각하고 그것을 React형식의 event로 변경후
  //어떤 Element가 이벤트을 발생시키지 지정해주는 것이<HTMLInputElement>이다.
  //결과적으로 inputElement가 onchange함수를 실행시킨다는 것이다.
  return(
    <>
      <form>
        <input value={value} onChange={onChange} type="text" placeholder = "username" />
        {/* //input의 value값의 useState의 value 값으로 들어간다. */}
        {/* 여기서 onChange는 input의 이벤트속성이고 그것이 행해질때 위의 onChange 함수를 사용하다는 것이다 */}
        <button>로그인</button>
      </form>
    </> 
  )
}
export default App;
