import styled from "styled-components";

interface ContainerProps {
  bgColor:string;
  borderColor?:string;
}

const Container = styled.div<ContainerProps>`
height: 100px;
width: 100px;
background-color: ${(props) => props.bgColor};
border-radius: 100px;
border : 1px solid ${(props) => props.borderColor}
`;

interface CircleProps {
  bgColor:string;
  borderColor?:string;
}
//?:를 붙여주면 있어도 되고 없어도 된다. :만 있으면 무조건 속성을 지정해줘야 한다.
const Circle = ({bgColor, borderColor}:CircleProps) =>{
  return(
    <Container bgColor = {bgColor} borderColor = {borderColor}/>

  )
} 
//Container를 만들어주고 bgColor에 해당하는 정보타입을 interface로 지정해준다.
//interface의 이름을 지정해주고 해당 props의 정보타입을 적어준다. 만든 interface를 다시 bgColor에 설정해줌으로써 정보타입을 지정해주는 것이다.
//Circle은 컴포넌트를 지정해준거고 컨테이너는 내가 사용할 div를 설정해준 것
export default Circle;
