import { useQuery } from "react-query";
import { fetchCoinHistory } from "./api";
import ApexChart from "react-apexcharts";

interface IHistory {
  time_open: string;
  time_close: string;
  open: number;
  high: number;
  low: number;
  close: number;
  volume: number;
  market_cap: number;
}
interface ChartProps {
  coinId: string;
}

const Chart = ({ coinId }: ChartProps) => {
  const { isLoading, data } = useQuery<IHistory>(["ohlcv", coinId], () =>
    fetchCoinHistory(coinId)
  );
  return (
    <>
     {isLoading ? "차트 생성중" : <ApexChart type="candlestick" series= {[
       {
         data:[[data?.time_open], [data?.open, data?.high, data?.low, data?.close]],
       },
     ]}
     options={{
       chart:{
         type:"candlestick",
         height: 300,
         width:500,
       },
       title: {
        text: 'CandleStick Chart',
        align: 'left'
      },
       yaxis: {
        show: false,
      },
      xaxis: {
        axisBorder: { show: false },
        axisTicks: { show: false },
        labels: { show: false },
      }
     }}
    />
    }
    </>
  );
};

export default Chart;
