import { Link } from "react-router-dom";
import styled from "styled-components";

const Container = styled.div``;
const Header = styled.header`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 15vh;
`;
const Title = styled.h1`
  font-size: 2.5em;
`;
const CoinsList = styled.ul`
  width: 768px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 25% 25% 25% 25%;
  justify-content: center;
  align-items: center;
  grid-gap: 2em;
`;
const Coin = styled.li`
  border: 2px solid ${(props) => props.theme.accentColor};
  line-height: 10vh;
  text-align: center;
  color: inherit;
  display: center;
  &:hover {
    background-color: black;
    border-radius: 15px;
    border: 0;
    a{
    color: ${(props) => props.theme.accentColor};
  }
  }
  a {
    display: block;
    transition: color 0.2s ease-in;
    color:${(props) => props.theme.textColor};;
  }
`;

const coins = [
  {
    id: "btc-bitcoin",
    name: "Bitcoin",
    symbol: "BTC",
    rank: 1,
    is_new: false,
    is_active: true,
    type: "coin",
  },
  {
    id: "eth-ethereum",
    name: "Ethereum",
    symbol: "ETH",
    rank: 2,
    is_new: false,
    is_active: true,
    type: "coin",
  },
  {
    id: "hex-hex",
    name: "HEX",
    symbol: "HEX",
    rank: 3,
    is_new: false,
    is_active: true,
    type: "token",
  },
  {
    id: "hehex",
    name: "HX",
    symbol: "HEX",
    rank: 3,
    is_new: false,
    is_active: true,
    type: "token",
  },
];

const Coins = () => {
  return (
    <Container>
      <Header>
        <Title>모든 코인</Title>
      </Header>
      <CoinsList>
        {coins.map((coin) => (
          <Coin key={coin.id}>
            <Link to={`/${coin.id}`}> {coin.name} </Link>
          </Coin>
        ))}
      </CoinsList>
    </Container>
  );
};
export default Coins;
