import { useQuery } from "react-query";
import styled from "styled-components";
import { fetchCoinPrcice } from "./api";

interface PriceProps {
  coinId: string;
}
interface IPrice {
  id: string;
  name: string;
  symbol: string;
  rank: number;
  price_usd: number;
  price_btc: number;
  volume_24h_usd: number;
  market_cap_usd: number;
  circulating_supply: number;
  total_supply: number;
  max_supply: number;
  percent_change_1h: number;
  percent_change_24h: number;
  percent_change_7d: number;
  last_updated: number;
}
const LoadingText = styled.div`
text-align:center;
font-size: 2rem;
`;
const Cantainer = styled.div``;
const PriceVeiw = styled.ul`

`;
const PriceItem = styled.li`
display: flex;
  align-items: center;
  justify-content: space-around;
  `;

const Price = ({ coinId }: PriceProps) => {
  const { isLoading, data } = useQuery<IPrice>(["money", coinId], () =>
    fetchCoinPrcice(coinId)
  );
  return (
    <div>
      {isLoading ? (
        <LoadingText>가격 측정중</LoadingText>
      ) : (
        <Cantainer>
          <PriceVeiw>
            <PriceItem><span>가격(USD)</span>{data?.price_usd}</PriceItem>
            <PriceItem><span>총 공급량</span>{data?.total_supply}</PriceItem>
            <PriceItem><span>하루 전</span>{data?.max_supply}</PriceItem>
            <PriceItem><span>일주일 전</span>{data?.percent_change_7d}</PriceItem>
          </PriceVeiw>
        </Cantainer>
      )}
    </div>
  );
};

export default Price;
